import { useState } from 'react';
import cors from 'cors';
import api from '../services/api'
import express from 'express';
import axios from 'axios';

export default class Form extends Component {

  constructor(props: any) {
    super(props);
    this.state = {
      nome: "",
      email: "",
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleNome = this.handleNome.bind(this);
    this.handleEmail = this.handleEmail.bind(this);
  }

  handleNome(e:any) {
    this.setState({ nome: e.target.value });
  }

  handleEmail(e:any) {
    this.setState({ email: e.target.value });
  }
  async handleSubmit(e:any) {
    const header = {
      headers: { "Candidate": "lksato@uol.com.br",
              },
    };
    const host =
      "https://85k7bv04na.execute-api.us-east-1.amazonaws.com/dev/register";

    const dados = { name:this.state.nome , Email: this.state.email };

    if (this.state.nome.valueOf !== [''] && this.state.email.valueOf !== ['']) {
      axios
        .post(host, dados, header)
        .then((resposta) => {
          console.log(resposta);
          alert("Cadastro realizado! Bem vindo ao neovision!");
        })
        .catch((error) => {
          console.log(error);
        });
    } 
    else {
    alert("Nome invalido ou e-mail invalido");
    }
    e.preventDefault();
  }
      render(){ 

        return (

                <form method="post" id="post"  onSubmit={this.handleSubmit} >
                 <div className="input">
              <input id="Nome"   value={this.state.nome} onChange={(e) => this.setState({ Nome: e.target.value })} placeholder="name" type="text"></input>
              <input id="Email"   value={this.state.email} onChange={this.handleEmail} placeholder="e-mail" type="Email"></input>
              <input id="btn" type="submit" style={{ backgroundColor: 'orange' }}></input>
                </div>
              </form>
              )
  }
}
