            import React,{ChangeEvent, FormEvent,Component} from 'react';
import { useState } from 'react';
import cors from 'cors';
import api from '../services/api'
import express from 'express';
import axios from 'axios';


export default class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nome: "",
      email: "",
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleNome = this.handleNome.bind(this);
    this.handleEmail = this.handleEmail.bind(this);
  }

  handleNome(e) {
    this.setState({ nome: e.target.value });
  }

  handleEmail(e) {
    this.setState({ email: e.target.value });
  }

  async handleSubmit(e) {
    const url =
      "https://85k7bv04na.execute-api.us-east-1.amazonaws.com/dev/register";
    const data = { name: this.state.nome, email: this.state.email };
    const options = {
      headers: { Authorization: "joaopaulong@hotmail.com" },
    };

    if (this.state.nome.length > 3 && this.state.email.length > 3) {
      axios
        .post(url, data, options)
        .then((r) => {
          console.log(r);
          alert("Cadastro realizado com sucesso!");
        })
        .catch((error) => {
          console.log("error", error);
        });
    } else {
      alert("Nome ou Email inválidos (Mínimo 4 Caracteres)");
    }

    e.preventDefault();
  }

  render() {
    return (

                <form method="post" id="post" >
                 <div className="input">
              <input id="Nome"    placeholder="name" type="text"></input>
              <input id="Email"   placeholder="e-mail" type="Email"></input>
              <input id="btn" type="submit" style={{ backgroundColor: 'orange' }}></input>
                </div>
              </form>
              )
  }}
