            import React,{Component} from 'react';
            import axios from 'axios';
            import cors from 'cors';

export default class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nome: '',
      email: '',
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleNome = this.handleNome.bind(this);
    this.handleEmail = this.handleEmail.bind(this);
  }
  handleNome(values) {
    this.setState({ nome: values.target.value });
  }

  handleEmail(values) {
    this.setState({ email: values.target.value });
  }

  async handleSubmit(values) {
    const header = {
      headers: { "Access-Control-Allow-Origin": "*",
      "Access-Control-Allow-Headers": "Authorization", 
      "Access-Control-Allow-Methods": "GET, POST, OPTIONS, PUT, PATCH, DELETE" ,
      "Content-Type": "application/json;charset=UTF-8",
      Authorization: "lksato@uol.com.br"
    },
    };
    const host =
      "https://85k7bv04na.execute-api.us-east-1.amazonaws.com/dev/register";

    const dados = { name: this.state.nome, email: this.state.email };

    if (this.state.nome !== '' && this.state.email !== '') {
      axios
        .POST(host, dados, header)
        .then((resposta) => {
          console.log(resposta);
          alert("Cadastro realizado! Bem vindo ao neovision!");
        })
        .catch((error) => {
          console.log(error);
        });
    } else {
      alert("Nome invalido ou e-mail invalido");
    }

    values.preventDefault();
  }
render(){
 return(

                <form method="post" id="post" onSubmit={this.handleSubmit}>
                 <div className="input">
              <input id="nome" placeholder="name" onChange={(values) => this.setState({ values: e.target.value }) }type="text"></input>
              <input id="email" placeholder="e-mail"onChange={(values) => this.setState({ values: e.target.value }) }type="email"></input>
              <input id="btn" type="submit" style={{ backgroundColor: 'orange' }}></input>
                </div>
              </form>
              )
 }}
