            import React,{ChangeEvent, FormEvent,Component} from 'react';
import { useState } from 'react';
import api from '../services/api'
import cors from 'cors'
import app from 'express'

export default function Form() {
 const [nome, setName] = useState('');
 const [email, setEmail] = useState('');


 async function handleSubmit(event : FormEvent) {
  event.preventDefault();

  const data = new FormData();
  data.append('nome', nome);
  data.append('email', email);
  const options = {
    headers: { Authorization: "lksato@uol.com.br" },
  };


  api.post('', data, options);
  alert('Cadastro realizado com sucesso!');
 }
  return(

                <form method="post" id="post" onSubmit={handleSubmit} >
                 <div className="input">
              <input id="nome"  value={nome}  onChange={e => setName(e.target.value)}  placeholder="name" type="text"></input>
              <input id="email"  value={email} onChange={e => setEmail(e.target.value)} placeholder="e-mail" type="email"></input>
              <input id="btn" type="submit" style={{ backgroundColor: 'orange' }}></input>
                </div>
              </form>
              )
  };
