            import React,{Component} from 'react';
            import axios from 'axios';

export default class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nome: '',
      email: '',
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleNome = this.handleNome.bind(this);
    this.handleEmail = this.handleEmail.bind(this);
  }
  handleNome(e) {
    this.setState({ nome: e.target.value });
  }

  handleEmail(e) {
    this.setState({ email: e.target.value });
  }

  async handleSubmit(e) {
    const header = {
      headers: { "candidate" : "lksato@uol.com.br" },
    };
    const host =
      "https://85k7bv04na.execute-api.us-east-1.amazonaws.com/dev/register";

    const dados = { name: this.state.nome, email: this.state.email };

    if (this.state.nome != '' && this.state.email != '') {
      axios
        .POST(host, dados, header)
        .then((resposta) => {
          console.log(resposta);
          alert("Cadastro realizado! Bem vindo ao neovision!");
        })
        .catch((error) => {
          console.log(error);
        });
    } else {
      alert("Nome invalido ou e-mail invalido");
    }

    e.preventDefault();
  }
render(){
 return(

                <form method="post">
                 <div className="input">
              <input id="nome" placeholder="name" onSubmit={this.handleSubmit} type="text"></input>
              <input id="email" placeholder="e-mail" type="email"></input>
              <input id="btn" type="submit" style={{ backgroundColor: 'orange' }}></input>
                </div>
              </form>
              )
 }}
